namespace MU\BoardModule\Event\Base;

use Symfony\Component\EventDispatcher\Event;
use MU\BoardModule\Entity\AboEntity;

/**
 * Event base class for filtering abo processing.
 */
class AbstractFilterAboEvent extends Event
{
    /**
     * @var AboEntity Reference to treated entity instance.
     */
    protected $abo;

    /**
     * @var array Entity change set for preUpdate events.
     */
    protected $entityChangeSet = [];

    /**
     * FilterAboEvent constructor.
     *
     * @param AboEntity $abo Processed entity
     * @param array $entityChangeSet Change set for preUpdate events
     */
    public function __construct(AboEntity $abo, array $entityChangeSet = [])
    {
        $this->abo = $abo;
        $this->entityChangeSet = $entityChangeSet;
    }

    /**
     * Returns the entity.
     *
     * @return AboEntity
     */
    public function getAbo()
    {
        return $this->abo;
    }

    /**
     * Returns the change set.
     *
     * @return array Entity change set
     */
    public function getEntityChangeSet()
    {
        return $this->entityChangeSet;
    }
}
