namespace MU\BoardModule\Event\Base;

use Symfony\Component\EventDispatcher\Event;
use MU\BoardModule\Entity\PostingEntity;

/**
 * Event base class for filtering posting processing.
 */
class AbstractFilterPostingEvent extends Event
{
    /**
     * @var PostingEntity Reference to treated entity instance.
     */
    protected $posting;

    /**
     * @var array Entity change set for preUpdate events.
     */
    protected $entityChangeSet = [];

    /**
     * FilterPostingEvent constructor.
     *
     * @param PostingEntity $posting Processed entity
     * @param array $entityChangeSet Change set for preUpdate events
     */
    public function __construct(PostingEntity $posting, array $entityChangeSet = [])
    {
        $this->posting = $posting;
        $this->entityChangeSet = $entityChangeSet;
    }

    /**
     * Returns the entity.
     *
     * @return PostingEntity
     */
    public function getPosting()
    {
        return $this->posting;
    }

    /**
     * Returns the change set.
     *
     * @return array Entity change set
     */
    public function getEntityChangeSet()
    {
        return $this->entityChangeSet;
    }
}
