namespace MU\BoardModule\Event\Base;

use Symfony\Component\EventDispatcher\Event;
use MU\BoardModule\Entity\RankEntity;

/**
 * Event base class for filtering rank processing.
 */
class AbstractFilterRankEvent extends Event
{
    /**
     * @var RankEntity Reference to treated entity instance.
     */
    protected $rank;

    /**
     * @var array Entity change set for preUpdate events.
     */
    protected $entityChangeSet = [];

    /**
     * FilterRankEvent constructor.
     *
     * @param RankEntity $rank Processed entity
     * @param array $entityChangeSet Change set for preUpdate events
     */
    public function __construct(RankEntity $rank, array $entityChangeSet = [])
    {
        $this->rank = $rank;
        $this->entityChangeSet = $entityChangeSet;
    }

    /**
     * Returns the entity.
     *
     * @return RankEntity
     */
    public function getRank()
    {
        return $this->rank;
    }

    /**
     * Returns the change set.
     *
     * @return array Entity change set
     */
    public function getEntityChangeSet()
    {
        return $this->entityChangeSet;
    }
}
