namespace MU\BoardModule\Helper\Base;

use IntlDateFormatter;
use Symfony\Component\HttpFoundation\RequestStack;
use Zikula\Common\Translator\TranslatorInterface;
use MU\BoardModule\Entity\CategoryEntity;
use MU\BoardModule\Entity\ForumEntity;
use MU\BoardModule\Entity\PostingEntity;
use MU\BoardModule\Entity\AboEntity;
use MU\BoardModule\Entity\UserEntity;
use MU\BoardModule\Entity\RankEntity;
use MU\BoardModule\Helper\ListEntriesHelper;

/**
 * Entity display helper base class.
 */
abstract class AbstractEntityDisplayHelper
{
    /**
     * @var TranslatorInterface
     */
    protected $translator;

    /**
     * @var ListEntriesHelper Helper service for managing list entries
     */
    protected $listEntriesHelper;

    /**
     * @var IntlDateFormatter Formatter for dates
     */
    protected $dateFormatter;

    /**
     * EntityDisplayHelper constructor.
     *
     * @param TranslatorInterface $translator        Translator service instance
     * @param RequestStack        $requestStack      RequestStack service instance
     * @param ListEntriesHelper   $listEntriesHelper Helper service for managing list entries
     */
    public function __construct(
        TranslatorInterface $translator,
        RequestStack $requestStack,
        ListEntriesHelper $listEntriesHelper
    ) {
        $this->translator = $translator;
        $this->listEntriesHelper = $listEntriesHelper;
        $locale = null !== $requestStack->getCurrentRequest() ? $requestStack->getCurrentRequest()->getLocale() : null;
        $this->dateFormatter = new IntlDateFormatter($locale, IntlDateFormatter::NONE, IntlDateFormatter::NONE);
    }

    /**
     * Returns the formatted title for a given entity.
     *
     * @param object $entity The given entity instance
     *
     * @return string The formatted title
     */
    public function getFormattedTitle($entity)
    {
        if ($entity instanceof CategoryEntity) {
            return $this->formatCategory($entity);
        }
        if ($entity instanceof ForumEntity) {
            return $this->formatForum($entity);
        }
        if ($entity instanceof PostingEntity) {
            return $this->formatPosting($entity);
        }
        if ($entity instanceof AboEntity) {
            return $this->formatAbo($entity);
        }
        if ($entity instanceof UserEntity) {
            return $this->formatUser($entity);
        }
        if ($entity instanceof RankEntity) {
            return $this->formatRank($entity);
        }
    
        return '';
    }
    
    /**
     * Returns the formatted title for a given entity.
     *
     * @param CategoryEntity $entity The given entity instance
     *
     * @return string The formatted title
     */
    protected function formatCategory(CategoryEntity $entity)
    {
        return $this->translator->__f('%title%', [
            '%title%' => $entity->getTitle()
        ]);
    }
    
    /**
     * Returns the formatted title for a given entity.
     *
     * @param ForumEntity $entity The given entity instance
     *
     * @return string The formatted title
     */
    protected function formatForum(ForumEntity $entity)
    {
        return $this->translator->__f('%title%', [
            '%title%' => $entity->getTitle()
        ]);
    }
    
    /**
     * Returns the formatted title for a given entity.
     *
     * @param PostingEntity $entity The given entity instance
     *
     * @return string The formatted title
     */
    protected function formatPosting(PostingEntity $entity)
    {
        return $this->translator->__f('%title%', [
            '%title%' => $entity->getTitle()
        ]);
    }
    
    /**
     * Returns the formatted title for a given entity.
     *
     * @param AboEntity $entity The given entity instance
     *
     * @return string The formatted title
     */
    protected function formatAbo(AboEntity $entity)
    {
        return $this->translator->__f('%userid%', [
            '%userid%' => $entity->getUserid()
        ]);
    }
    
    /**
     * Returns the formatted title for a given entity.
     *
     * @param UserEntity $entity The given entity instance
     *
     * @return string The formatted title
     */
    protected function formatUser(UserEntity $entity)
    {
        return $this->translator->__f('%userid%', [
            '%userid%' => $entity->getUserid()
        ]);
    }
    
    /**
     * Returns the formatted title for a given entity.
     *
     * @param RankEntity $entity The given entity instance
     *
     * @return string The formatted title
     */
    protected function formatRank(RankEntity $entity)
    {
        return $this->translator->__f('%name%', [
            '%name%' => $entity->getName()
        ]);
    }
    
    /**
     * Returns name of the field used as title / name for entities of this repository.
     *
     * @param string $objectType Name of treated entity type
     *
     * @return string Name of field to be used as title
     */
    public function getTitleFieldName($objectType)
    {
        if ($objectType == 'category') {
            return 'title';
        }
        if ($objectType == 'forum') {
            return 'title';
        }
        if ($objectType == 'posting') {
            return 'title';
        }
        if ($objectType == 'abo') {
            return '';
        }
        if ($objectType == 'user') {
            return '';
        }
        if ($objectType == 'rank') {
            return 'name';
        }
    
        return '';
    }
    
    /**
     * Returns name of the field used for describing entities of this repository.
     *
     * @param string $objectType Name of treated entity type
     *
     * @return string Name of field to be used as description
     */
    public function getDescriptionFieldName($objectType)
    {
        if ($objectType == 'category') {
            return 'description';
        }
        if ($objectType == 'forum') {
            return 'description';
        }
        if ($objectType == 'posting') {
            return 'text';
        }
        if ($objectType == 'abo') {
            return '';
        }
        if ($objectType == 'user') {
            return '';
        }
        if ($objectType == 'rank') {
            return 'name';
        }
    
        return '';
    }
    
    /**
     * Returns name of first upload field which is capable for handling images.
     *
     * @param string $objectType Name of treated entity type
     *
     * @return string Name of field to be used for preview images
     */
    public function getPreviewFieldName($objectType)
    {
        if ($objectType == 'posting') {
            return 'firstImage';
        }
        if ($objectType == 'rank') {
            return 'uploadImage';
        }
    
        return '';
    }
    
    /**
     * Returns name of the date(time) field to be used for representing the start
     * of this object. Used for providing meta data to the tag module.
     *
     * @param string $objectType Name of treated entity type
     *
     * @return string Name of field to be used as date
     */
    public function getStartDateFieldName($objectType)
    {
        if ($objectType == 'category') {
            return 'createdDate';
        }
        if ($objectType == 'forum') {
            return 'createdDate';
        }
        if ($objectType == 'posting') {
            return 'createdDate';
        }
        if ($objectType == 'abo') {
            return 'createdDate';
        }
        if ($objectType == 'user') {
            return '';
        }
        if ($objectType == 'rank') {
            return 'createdDate';
        }
    
        return '';
    }
}
