/**
 * The muboardmoduleObjectTypeSelector plugin provides items for a dropdown selector.
 *
 * Available parameters:
 *   - assign: If set, the results are assigned to the corresponding variable instead of printed out.
 *
 * @param  array            $params All attributes passed to this function from the template
 * @param  Zikula_Form_View $view   Reference to the view object
 *
 * @return string The output of the plugin
 */
function smarty_function_muboardmoduleObjectTypeSelector($params, $view)
{
    $dom = ZLanguage::getModuleDomain('MUBoardModule');
    $result = [];

    $result[] = [
        'text' => __('Categories', $dom),
        'value' => 'category'
    ];
    $result[] = [
        'text' => __('Forums', $dom),
        'value' => 'forum'
    ];
    $result[] = [
        'text' => __('Postings', $dom),
        'value' => 'posting'
    ];
    $result[] = [
        'text' => __('Abos', $dom),
        'value' => 'abo'
    ];
    $result[] = [
        'text' => __('Users', $dom),
        'value' => 'user'
    ];
    $result[] = [
        'text' => __('Ranks', $dom),
        'value' => 'rank'
    ];

    if (array_key_exists('assign', $params)) {
        $view->assign($params['assign'], $result);

        return;
    }

    return $result;
}
