    /**
     * Uninstall MUBoard.
     *
     * @return boolean True on success, false otherwise.
     */
    public function uninstall()
    {
        // delete stored object workflows
        $result = Zikula_Workflow_Util::deleteWorkflowsForModule($this->getName());
        if ($result === false) {
            return LogUtil::registerError($this->__f('An error was encountered while removing stored object workflows for the %s module.', array($this->getName())));
        }

        try {
            DoctrineHelper::dropSchema($this->entityManager, $this->listEntityClasses());
        } catch (Exception $e) {
            if (System::isDevelopmentMode()) {
                LogUtil::registerError($this->__('Doctrine Exception: ') . $e->getMessage());
            }
            return LogUtil::registerError($this->__f('An error was encountered while dropping the tables for the %s module.', array($this->getName())));
        }

        // unregister persistent event handlers
        EventUtil::unregisterPersistentModuleHandlers('MUBoard');

        // unregister hook subscriber bundles
        HookUtil::unregisterSubscriberBundles($this->version->getHookSubscriberBundles());

        // remove all module vars
        $this->delVars();

        // deletion successful
        return true;
    }

    /**
     * Build array with all entity classes for MUBoard.
     *
     * @return array list of class names.
     */
    protected function listEntityClasses()
    {
        $classNames = array();
        $classNames[] = 'MUBoard_Entity_Category';
        $classNames[] = 'MUBoard_Entity_Forum';
        $classNames[] = 'MUBoard_Entity_Posting';
        $classNames[] = 'MUBoard_Entity_Abo';
        $classNames[] = 'MUBoard_Entity_User';
        $classNames[] = 'MUBoard_Entity_Rank';

        return $classNames;
    }
    /**
     * Create the default data for MUBoard.
     *
     * @return void
     */
    protected function createDefaultData()
    {
        // Ensure that tables are cleared
        $this->entityManager->transactional(function ($entityManager)
        {
            $entityManager->getRepository('MUBoard_Entity_Category')->truncateTable();
            $entityManager->getRepository('MUBoard_Entity_Forum')->truncateTable();
            $entityManager->getRepository('MUBoard_Entity_Posting')->truncateTable();
            $entityManager->getRepository('MUBoard_Entity_Abo')->truncateTable();
            $entityManager->getRepository('MUBoard_Entity_User')->truncateTable();
            $entityManager->getRepository('MUBoard_Entity_Rank')->truncateTable();
        }
        );

        // Insertion successful
        return true;
    }

